$(function(){var pumpType;$('#pumpModal').on('hidden.bs.modal',function(){$('#pumpModal .pump-types button').removeClass('btn-primary').removeClass('btn-info').addClass('btn-info');$('#pumpModal .pump-options').addClass('hide');});$('#pumpModal .pump-types button').click(function(){$('#pumpModal .pump-types button').removeClass('btn-primary').removeClass('btn-info').addClass('btn-info');$(this).removeClass('btn-info').addClass('btn-primary');$('#pumpModal .pump-options').removeClass('hide');pumpType=$(this).data('value');return false;});$('#pumpModal button.save').click(function(){trackEvent($('#pumpModal'),'Pump',pumpType,$(this).parent().find('.spinner').data('value'));return false;});});$(function(){$('.spinner .spinner-left').click(function(){var currentValue=$(this).parent().data('value');var step=$(this).parent().data('step');var newValue=currentValue-=step;if(newValue>0){$(this).parent().data('value',newValue);$(this).parent().find('.amount').html(formatDecimal(newValue));}});$('.spinner .spinner-right').click(function(){var currentValue=$(this).parent().data('value');var step=$(this).parent().data('step');var newValue=currentValue+=step;$(this).parent().data('value',newValue);$(this).parent().find('.amount').html(formatDecimal(newValue));});});function formatDecimal(value){var integerPart=Math.floor(value);var decimalPart=value-integerPart;if(decimalPart==0.25){return integerPart+'&frac14;';}else if(decimalPart==0.5){return integerPart+'&frac12;';}else if(decimalPart==0.75){return integerPart+'&frac13;';}else{return value;}}
$(function(){updateStats();setInterval('updateStats()',1000);});function updateStats()
{$.get('track/stats',function(response){$('.eventbutton-feed').find('.badge').html(response.feed.time);$('.eventbutton-pump').find('.badge').html(response.pump.time);$('.eventbutton-diaper').find('.badge').html(response.diaper.time);});}
function trackEvent(activeModal,type,subtype,value)
{var spinner=$('<i>').addClass('icon-spinner').addClass('icon-spin').addClass('icon-2x');activeModal.find('.modal-body').append(spinner);$.post('track/new',{type:type,subtype:subtype,value:value},function(response){spinner.remove();if(response.success&&response.success==true){showNotification(type);activeModal.modal('hide');updateStats();}else{showNotification(type,true);}});}